mapper.select:
/*selectBoardCnt*/
select
	count(*) cnt
from e_board a
where
a.bbs_id = #{bbsId}

mapper.update:
/*updateBoardHitCount*/
update e_board set
	hit = hit +1
where 
	board_seq = #{boardSeq}

/*updateBoardGoodCount*/
update e_board set
	good_cnt = good_cnt +1
where 
	board_seq = #{boardSeq}

/*updateBoardBadCount*/
update e_board set
	bad_cnt = bad_cnt +1
where 
	board_seq = #{boardSeq}
