mapper.update:
/*updateMngrManagerProc*/
update e_member 
set 
member_pw = #{memberPw}
where
member_id = #{memberId}

mapper.select:
select
	member_id memberId, 
	member_pw memberPw,
	member_pwc memberPwc,
	member_nickname memberNickname,
	member_birth memberBrith,
	member_gender memberGender
	member_way memberWay
from e_member
where 
	member_id = #{memberId}
	and
	member_pw = #{memberPw}
	and
	member_pwc = #{memberPwc}
	and
	member_nickname = #{memberNickname}
	and
	member_birth = #{memberBrith}
	and
	member_gender = #{memberGender}
	and
	member_way = #{memberWay}
